class Manga{
  final String mangaTitle;
  final String mangaImageUrl;
  final String mangaYear;
  final String mangaDescription;

  final String mangaGenres;

  Manga({required this.mangaTitle, required this.mangaImageUrl, required this.mangaYear, required this.mangaDescription, this.mangaGenres = ''} );
  
    @override
  bool operator ==(Object other) {
    if (identical(this, other)) return true;
    return other is Manga &&
        other.mangaTitle == mangaTitle &&
        other.mangaImageUrl == mangaImageUrl; // Добавлено сравнение по изображению
  }

  @override
  int get hashCode => mangaTitle.hashCode ^ mangaImageUrl.hashCode; 
}

